function show_copy_area( type, value, obj_id ) {
    var o = jQuery( obj_id );
    result = '';
    switch(type) {
        case 'video':
            result = '<object classid="clsid:D27CDB6E-AE6D-11cf-96B8-444553540000" width="640" height="520">'
                            +'<param name="movie" value="' + value + '&config=" />'
                            +'<param name="quality" value="high" />'
                            +'<param name="wmode" value="opaque" />'
                            +'<param name="allowfullscreen" value="true" />'
                            +'<param name="allowscriptaccess" value="always" />'
                            +'<param name="swfversion" value="8,0,0,0" />'
                            +'<!--[if !IE]>--><object type="application/x-shockwave-flash" data="' + value + '&config=" width="640" height="520"><!--<![endif]-->'
                            +'<param name="quality" value="high" />'
                            +'<param name="wmode" value="opaque" />'
                            +'<param name="swfversion" value="8,0,0,0" />'
                          +'</object>'
                        +'</object> ';
    }
    
    o.text(result);
    if(o.css('display') == 'none') {
        o.slideDown('slow');
    }
    else {
        o.slideToggle('slow');
    }
}

