;$(document).ready(function(){
    var parent = $('.menu_parent');
    var parent_width = 980;
    var childs_width_sum = 0;

    var childs = $('.menu-i', parent);
    childs.each(function(){
        childs_width_sum += $(this).width() + 5; /* 5 - padding */
    });
    var free_space = parent_width - childs_width_sum;
    if( free_space > 0){
        var space_on_item = Math.floor(free_space / childs.length);
        var adder = space_on_item / 2;
        if (adder % 2) {
            adder = Math.floor(adder);
            childs.each(function(){
                $('.menu-i-link', this).css({
                    paddingLeft: '+=' + adder,
                    paddingRight: '+=' +  (adder + 1)
                })
            });
        } else {
            childs.each(function(){
                $('.menu-i-link', this).css({
                    paddingLeft: '+=' + adder,
                    paddingRight: '+=' + adder
                })
            });
        }
        var last_adder = (free_space - (Math.floor(free_space / childs.length) * childs.length)) / 2;
        if( last_adder % 2){
            last_adder = Math.floor(last_adder);
            $('.menu-i:last .menu-i-link', parent).css({
                paddingLeft: '+=' + (last_adder + 1),
                paddingRight: '+=' + last_adder
            });
        } else {
            $('.menu-i:last .menu-i-link', parent).css({
                paddingLeft: '+=' + last_adder,
                paddingRight: '+=' + last_adder
            });
        }
    }
});
