(function($) {
    function set_slide (new_index, old_index){
        if (new_index == old_index) return;

        $('.slider-container .slide.current').fadeOut('slow');
        $('.slider-container .current').removeClass('current');
        $('.slide:eq('+new_index+')').fadeIn('slow', function(){
            $(this).addClass('current');
        });
        $('.slider-status span:eq('+new_index+')').addClass('current');
    }

    function go_next_slide(){
        var old_index = $('.slide').index($('.slider .current'));
        var items_cnt = $('.slide').length;
        var new_index = (old_index < items_cnt - 1) ? (old_index + 1) : 0;
        set_slide(new_index, old_index);
    }

    function go_prev_slide(){
        var old_index = $('.slide').index($('.slider .current'));
        var items_cnt = $('.slide').length;
        var new_index = (old_index == 0) ? (items_cnt-1) : (old_index - 1);
        set_slide(new_index, old_index);
    }

    $(document).ready(function() {
        var slides_cnt = $('.slide').length;
        var slider_container = $('.slider-container');
        var previews = '';
        for (var i = 1;i <= slides_cnt; i++)
            previews += '<span>'+ i + '</span>';
        $('.slider-status').html(previews);
        $('.slide:first, .slider-status span:first').addClass('current');

        $('.slider-status span').live('click', function(){
            var old_index = $('.slide').index($('.slider .current'));
            var new_index = $('.slider-status span').index(this);
            set_slide(new_index, old_index);
        });

        // slider autochange
        function run_slider(){
            slider_SI = setInterval(go_next_slide, 5000);
        }
        run_slider();
        slider_container.hover(function(){
            clearInterval(slider_SI);
        }, function(){
            run_slider();
        });

        var startPos;
        var movePos;
        var delta;
        slider_container.on('touchstart', function(e){
            var touch = e.originalEvent.touches[0] || e.originalEvent.changedTouches[0];
            startPos = {
                pageX: touch.pageX,
                pageY: touch.pageY
            };
            clearInterval(slider_SI);
        });
        slider_container.on('touchmove', function(e){
            var touch = e.originalEvent.touches[0] || e.originalEvent.changedTouches[0];
            movePos = {
                pageX:touch.pageX,
                pageY:touch.pageY
            };
            delta = {
                x: movePos.pageX - startPos.pageX,
                y:movePos.pageY - startPos.pageY
            };

            if ((Math.abs(delta.x) > Math.abs(delta.y))){
                e.preventDefault();
            }
        });
        slider_container.on('touchend', function (e) {
            if (Math.abs(delta.x) > Math.abs(delta.y)) {
                if (delta.x < 0)
                    go_next_slide();
                else
                    go_prev_slide();
            }
            run_slider();
        });
    });

})(jQuery);
